export const SportIcon: FC<SportIconProps> = ({
  name,
  size = 24,
  color = 'currentColor',
  className,
}) => {
  const icon = icons[name];
  if (!icon) {
    console.warn(`Sport icon "${name}" not found`);
    return null;
  }

  return (
    <svg
      viewBox={icon.viewBox}
      width={size}
      height={size}
      fill="none"
      className={className}
      xmlns="http://www.w3.org/2000/svg"
    >
      {icon.paths({ color })}
    </svg>
  );
};
